by_name.map:
function (doc) {
  if (doc.type === 'doctor' && doc.personal_info && doc.personal_info.full_name) {
    emit(doc.personal_info.full_name.toLowerCase(), {
      id: doc._id,
      name: doc.personal_info.full_name,
      specialty: doc.professional_info ? doc.professional_info.specialty : null,
      phone: doc.personal_info.phone,
      experience: doc.professional_info ? doc.professional_info.experience_years : null,
      status: doc.status
    });
  }
}

by_specialty.map:
function (doc) {
  if (doc.type === 'doctor' && doc.professional_info && doc.professional_info.specialty) {
    emit(doc.professional_info.specialty, {
      id: doc._id,
      name: doc.personal_info ? doc.personal_info.full_name : null,
      experience: doc.professional_info.experience_years,
      phone: doc.personal_info ? doc.personal_info.phone : null
    });
  }
}

active.map:
function (doc) {
  if (doc.type === 'doctor' && doc.status === 'active') {
    emit(doc.personal_info ? doc.personal_info.full_name : null, {
      id: doc._id,
      name: doc.personal_info ? doc.personal_info.full_name : null,
      specialty: doc.professional_info ? doc.professional_info.specialty : null,
      phone: doc.personal_info ? doc.personal_info.phone : null
    });
  }
}

by_working_day.map:
function (doc) {
  if (doc.type === 'doctor' && doc.schedule && doc.schedule.working_days) {
    for (var i = 0; i < doc.schedule.working_days.length; i++) {
      var day = doc.schedule.working_days[i];
      emit(day, {
        id: doc._id,
        name: doc.personal_info ? doc.personal_info.full_name : null,
        specialty: doc.professional_info ? doc.professional_info.specialty : null,
        hours: doc.schedule.working_hours || null
      });
    }
  }
}

by_experience.map:
function (doc) {
  if (doc.type === 'doctor' && doc.professional_info && typeof doc.professional_info.experience_years === 'number') {
    var e = doc.professional_info.experience_years;
    var level = e < 5 ? 'junior' : (e < 15 ? 'senior' : 'expert');
    emit(level, {
      id: doc._id,
      name: doc.personal_info ? doc.personal_info.full_name : null,
      specialty: doc.professional_info.specialty,
      experience: e
    });
  }
}
