by_patient.map:
function (doc) {
  if (doc.type === 'invoice') {
    emit(doc.patient_id, {
      id: doc._id,
      invoice_number: doc.invoice_info.invoice_number,
      total_amount: doc.payment_info.total_amount,
      payment_status: doc.payment_status,
      invoice_date: doc.invoice_info.invoice_date
    });
  }
}

by_date.map:
function (doc) {
  if (doc.type === 'invoice') {
    var date = doc.invoice_info.invoice_date.split('T')[0];
    emit(date, {
      id: doc._id,
      patient_id: doc.patient_id,
      total_amount: doc.payment_info.total_amount,
      payment_status: doc.payment_status
    });
  }
}

by_status.map:
function (doc) {
  if (doc.type === 'invoice') {
    emit(doc.payment_status, {
      id: doc._id,
      patient_id: doc.patient_id,
      invoice_number: doc.invoice_info.invoice_number,
      total_amount: doc.payment_info.total_amount,
      due_date: doc.invoice_info.due_date
    });
  }
}

unpaid_invoices.map:
function (doc) {
  if (doc.type === 'invoice' && doc.payment_status !== 'paid') {
    emit(doc.invoice_info.due_date, {
      id: doc._id,
      patient_id: doc.patient_id,
      invoice_number: doc.invoice_info.invoice_number,
      total_amount: doc.payment_info.total_amount,
      days_overdue: Math.floor(
        (new Date() - new Date(doc.invoice_info.due_date)) /
        (1000 * 60 * 60 * 24)
      )
    });
  }
}

revenue_by_month.map:
function (doc) {
  if (doc.type === 'invoice' && doc.payment_status === 'paid') {
    var month = doc.invoice_info.invoice_date.substring(0, 7);
    emit(month, doc.payment_info.total_amount);
  }
}
