by_patient.map:
function (doc) {
  if (doc.type === 'medical_record') {
    emit(doc.patient_id, {
      id: doc._id,
      visit_date: doc.visit_info.visit_date,
      diagnosis: doc.diagnosis.primary.description,
      doctor_id: doc.doctor_id,
      status: doc.status
    });
  }
}

by_doctor.map:
function (doc) {
  if (doc.type === 'medical_record') {
    emit(doc.doctor_id, {
      id: doc._id,
      patient_id: doc.patient_id,
      visit_date: doc.visit_info.visit_date,
      diagnosis: doc.diagnosis.primary.description
    });
  }
}

by_diagnosis.map:
function (doc) {
  if (doc.type === 'medical_record' && doc.diagnosis.primary) {
    emit(doc.diagnosis.primary.code, {
      id: doc._id,
      patient_id: doc.patient_id,
      diagnosis: doc.diagnosis.primary.description,
      visit_date: doc.visit_info.visit_date,
      severity: doc.diagnosis.primary.severity
    });
  }
}

recent_visits.map:
function (doc) {
  if (doc.type === 'medical_record') {
    emit(doc.visit_info.visit_date, {
      id: doc._id,
      patient_id: doc.patient_id,
      doctor_id: doc.doctor_id,
      chief_complaint: doc.visit_info.chief_complaint,
      diagnosis: doc.diagnosis.primary.description
    });
  }
}
