by_patient.map:
function (doc) {
  if (doc.type === 'treatment') {
    emit(doc.patient_id, {
      id: doc._id,
      treatment_name: doc.treatment_info.treatment_name,
      start_date: doc.treatment_info.start_date,
      end_date: doc.treatment_info.end_date,
      status: doc.status
    });
  }
}

active_treatments.map:
function (doc) {
  if (doc.type === 'treatment' && doc.status === 'active') {
    emit(doc.patient_id, {
      id: doc._id,
      treatment_name: doc.treatment_info.treatment_name,
      start_date: doc.treatment_info.start_date,
      end_date: doc.treatment_info.end_date
    });
  }
}

by_medication.map:
function (doc) {
  if (doc.type === 'treatment' && doc.medications) {
    doc.medications.forEach(function(med) {
      emit(med.medication_id, {
        id: doc._id,
        patient_id: doc.patient_id,
        medication_name: med.name,
        dosage: med.dosage,
        frequency: med.frequency
      });
    });
  }
}
